import { Component, OnInit } from '@angular/core';
import { ToolService } from '../../services/tool.service';
import { CanvasService } from '../../services/canvas.service';
import { HistoryService } from '../../services/history.service';
import { ToolType } from '../../models/drawing.model';

@Component({
  selector: 'app-toolbar',
  templateUrl: './toolbar.component.html',
  styleUrls: ['./toolbar.component.scss']
})
export class ToolbarComponent implements OnInit {
  currentTool: ToolType = 'select';
  strokeColor = '#000000';
  fillColor = '#ffffff';
  strokeWidth = 2;
  canUndo = false;
  canRedo = false;
  snapToGrid = false;
  gridSize = 20;
  theme: 'light' | 'dark' = 'light';
  fontSize = 16;
  fontFamily = 'Arial';
  fontWeight: 'normal' | 'bold' = 'normal';
  fontStyle: 'normal' | 'italic' = 'normal';
  hasTextSelection = false;
  textColor = '#000000';

  tools: { type: ToolType; icon: string; label: string }[] = [
    { type: 'select', icon: '↖', label: 'Selecionar' },
    { type: 'pan', icon: '✋', label: 'Mover Canvas' },
    { type: 'rectangle', icon: '⬜', label: 'Retângulo' },
    { type: 'circle', icon: '⭕', label: 'Círculo' },
    { type: 'ellipse', icon: '⬭', label: 'Elipse' },
    { type: 'arrow', icon: '→', label: 'Seta' },
    { type: 'line', icon: '─', label: 'Linha' },
    { type: 'triangle', icon: '▲', label: 'Triângulo' },
    { type: 'star', icon: '★', label: 'Estrela' },
    { type: 'polygon', icon: '⬣', label: 'Polígono' },
    { type: 'freedraw', icon: '✏', label: 'Desenho Livre' },
    { type: 'text', icon: 'T', label: 'Texto' }
  ];

  constructor(
    public toolService: ToolService,
    private canvasService: CanvasService,
    private historyService: HistoryService
  ) {}

  ngOnInit(): void {
    this.toolService.currentTool$.subscribe(tool => {
      this.currentTool = tool;
    });

    this.toolService.strokeColor$.subscribe(color => {
      this.strokeColor = color;
      this.textColor = color;
    });

    this.toolService.fillColor$.subscribe(color => {
      this.fillColor = color;
    });

    this.toolService.strokeWidth$.subscribe(width => {
      this.strokeWidth = width;
    });

    this.toolService.fontSize$.subscribe(size => {
      this.fontSize = size;
    });

    this.toolService.fontFamily$.subscribe(family => {
      this.fontFamily = family;
    });

    this.toolService.fontWeight$.subscribe(weight => {
      this.fontWeight = weight;
    });

    this.toolService.fontStyle$.subscribe(style => {
      this.fontStyle = style;
    });

    this.historyService.canUndo$.subscribe(canUndo => {
      this.canUndo = canUndo;
    });

    this.historyService.canRedo$.subscribe(canRedo => {
      this.canRedo = canRedo;
    });

    this.canvasService.snapToGrid$.subscribe(enabled => {
      this.snapToGrid = enabled;
    });

    this.canvasService.gridSize$.subscribe(size => {
      this.gridSize = size;
    });

    this.canvasService.theme$.subscribe(theme => {
      this.theme = theme;
    });

    this.canvasService.elements$.subscribe(elements => {
      const selectedText = elements.find(el => el.isSelected && !!el.text);
      this.hasTextSelection = !!selectedText;

      if (selectedText) {
        if (selectedText.fontSize) this.toolService.setFontSize(selectedText.fontSize);
        if (selectedText.fontFamily) this.toolService.setFontFamily(selectedText.fontFamily);
        if (selectedText.fontWeight) this.toolService.setFontWeight(selectedText.fontWeight);
        if (selectedText.fontStyle) this.toolService.setFontStyle(selectedText.fontStyle);
        if (selectedText.strokeColor) {
          this.toolService.setStrokeColor(selectedText.strokeColor);
          this.textColor = selectedText.strokeColor;
        }
      }
    });
  }

  selectTool(tool: ToolType): void {
    this.toolService.setTool(tool);
  }

  onStrokeColorChange(color: string): void {
    this.toolService.setStrokeColor(color);
    this.applyTextStyleToSelection();
  }

  onTextColorChange(color: string): void {
    this.textColor = color;
    this.toolService.setStrokeColor(color);
    this.applyTextStyleToSelection(true);
  }

  onFillColorChange(color: string): void {
    this.toolService.setFillColor(color);
  }

  onStrokeWidthChange(width: number): void {
    this.toolService.setStrokeWidth(width);
  }

  onFontSizeChange(size: number): void {
    this.toolService.setFontSize(size);
    this.applyTextStyleToSelection();
  }

  onFontFamilyChange(family: string): void {
    this.toolService.setFontFamily(family);
    this.applyTextStyleToSelection();
  }

  toggleBold(): void {
    this.toolService.toggleBold();
    this.applyTextStyleToSelection();
  }

  toggleItalic(): void {
    this.toolService.toggleItalic();
    this.applyTextStyleToSelection();
  }

  applyTextStyleToSelection(force = false): void {
    if (!this.hasTextSelection && !force) return;
    this.canvasService.updateSelectedTextStyle({
      fontSize: this.fontSize,
      fontFamily: this.fontFamily,
      fontWeight: this.fontWeight,
      fontStyle: this.fontStyle,
      strokeColor: this.textColor
    });
    this.historyService.addState(this.canvasService.getState());
  }

  undo(): void {
    const state = this.historyService.undo();
    if (state) {
      this.canvasService.setState(state);
    }
  }

  redo(): void {
    const state = this.historyService.redo();
    if (state) {
      this.canvasService.setState(state);
    }
  }

  zoomIn(): void {
    this.canvasService.setZoom(this.canvasService.zoom * 1.2);
  }

  zoomOut(): void {
    this.canvasService.setZoom(this.canvasService.zoom / 1.2);
  }

  resetZoom(): void {
    this.canvasService.setZoom(1);
    this.canvasService.setPan({ x: 0, y: 0 });
  }

  toggleSnapToGrid(): void {
    this.canvasService.setSnapToGrid(!this.snapToGrid);
  }

  onGridSizeChange(size: number): void {
    this.canvasService.setGridSize(size);
  }

  deleteSelected(): void {
    this.canvasService.deleteSelectedElements();
    this.historyService.addState(this.canvasService.getState());
  }

  clearCanvas(): void {
    if (confirm('Tem certeza que deseja limpar todo o canvas?')) {
      this.canvasService.clear();
      this.historyService.clear();
      this.historyService.addState(this.canvasService.getState());
    }
  }

  rotateLeft(): void {
    this.canvasService.rotateSelected(-15);
    this.historyService.addState(this.canvasService.getState());
  }

  rotateRight(): void {
    this.canvasService.rotateSelected(15);
    this.historyService.addState(this.canvasService.getState());
  }

  align(direction: 'left' | 'right' | 'top' | 'bottom' | 'center' | 'middle'): void {
    this.canvasService.alignSelected(direction);
    this.historyService.addState(this.canvasService.getState());
  }

  distribute(orientation: 'horizontal' | 'vertical'): void {
    this.canvasService.distributeSelected(orientation);
    this.historyService.addState(this.canvasService.getState());
  }

  group(): void {
    this.canvasService.groupSelected();
    this.historyService.addState(this.canvasService.getState());
  }

  ungroup(): void {
    this.canvasService.ungroupSelected();
    this.historyService.addState(this.canvasService.getState());
  }

  bringToFront(): void {
    this.canvasService.bringToFront();
    this.historyService.addState(this.canvasService.getState());
  }

  sendToBack(): void {
    this.canvasService.sendToBack();
    this.historyService.addState(this.canvasService.getState());
  }

  applyTemplate(name: 'sticky-notes' | 'flow'): void {
    this.canvasService.insertTemplate(name);
    this.historyService.addState(this.canvasService.getState());
  }

  toggleTheme(): void {
    const next = this.theme === 'light' ? 'dark' : 'light';
    this.canvasService.setTheme(next);
  }

  exportToPNG(): void {
    const canvas = document.querySelector('canvas');
    if (canvas) {
      const link = document.createElement('a');
      link.download = 'whiteboard.png';
      link.href = canvas.toDataURL();
      link.click();
    }
  }

  exportToJSON(): void {
    const json = this.canvasService.exportToJSON();
    const blob = new Blob([json], { type: 'application/json' });
    const link = document.createElement('a');
    link.download = 'whiteboard.json';
    link.href = URL.createObjectURL(blob);
    link.click();
  }

  importFromJSON(): void {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = '.json';
    input.onchange = (e: any) => {
      const file = e.target.files[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = (event: any) => {
          const success = this.canvasService.importFromJSON(event.target.result);
          if (success) {
            this.historyService.clear();
            this.historyService.addState(this.canvasService.getState());
            alert('Importação realizada com sucesso!');
          } else {
            alert('Erro ao importar arquivo!');
          }
        };
        reader.readAsText(file);
      }
    };
    input.click();
  }
}
<div class="toolbar">
  <div class="toolbar-section">
    <h2 class="toolbar-title">🎨 Whiteboard</h2>
  </div>

  <div class="toolbar-section tools-section">
    <button
      *ngFor="let tool of tools"
      [class.active]="currentTool === tool.type"
      (click)="selectTool(tool.type)"
      [title]="tool.label"
      class="tool-button"
    >
      <span class="tool-icon">{{ tool.icon }}</span>
    </button>
  </div>

  <div class="toolbar-section colors-section">
    <div class="color-picker">
      <label>Contorno:</label>
      <input
        type="color"
        [value]="strokeColor"
        (input)="onStrokeColorChange($any($event.target).value)"
      />
    </div>
    <div class="color-picker">
      <label>Preenchimento:</label>
      <input
        type="color"
        [value]="fillColor"
        (input)="onFillColorChange($any($event.target).value)"
      />
    </div>
    <div class="color-picker text-color-picker">
      <label>Cor do texto:</label>
      <input
        type="color"
        [value]="textColor"
        (input)="onTextColorChange($any($event.target).value)"
      />
    </div>
    <div class="stroke-width">
      <label>Espessura:</label>
      <input
        type="range"
        min="1"
        max="10"
        [value]="strokeWidth"
        (input)="onStrokeWidthChange(+$any($event.target).value)"
      />
      <span>{{ strokeWidth }}px</span>
    </div>
    <div class="font-controls">
      <label>Tamanho:</label>
      <input
        type="number"
        min="8"
        max="72"
        [value]="fontSize"
        (input)="onFontSizeChange(+$any($event.target).value)"
      />
      <label>Fonte:</label>
      <select [value]="fontFamily" (change)="onFontFamilyChange($any($event.target).value)">
        <option>Arial</option>
        <option>Segoe UI</option>
        <option>Roboto</option>
        <option>Inter</option>
        <option>Georgia</option>
        <option>Courier New</option>
      </select>
      <button
        class="toggle"
        [class.active]="fontWeight === 'bold'"
        (click)="toggleBold()"
        title="Negrito"
      >B</button>
      <button
        class="toggle"
        [class.active]="fontStyle === 'italic'"
        (click)="toggleItalic()"
        title="Itálico"
      ><i>I</i></button>
      <button class="apply-style" (click)="applyTextStyleToSelection()" [disabled]="!hasTextSelection" title="Aplicar estilo ao texto selecionado">
        Aplicar ao selecionado
      </button>
    </div>
  </div>

  <div class="toolbar-section actions-section">
    <button (click)="undo()" [disabled]="!canUndo" title="Desfazer (Ctrl+Z)">
      ↶ Desfazer
    </button>
    <button (click)="redo()" [disabled]="!canRedo" title="Refazer (Ctrl+Y)">
      ↷ Refazer
    </button>
    <button (click)="zoomIn()" title="Aumentar Zoom">🔍+</button>
    <button (click)="zoomOut()" title="Diminuir Zoom">🔍-</button>
    <button (click)="resetZoom()" title="Resetar Zoom">100%</button>
    <button (click)="deleteSelected()" title="Deletar Selecionados (Delete)">
      🗑️ Deletar
    </button>
    <button (click)="clearCanvas()" title="Limpar Canvas">
      🗑️ Limpar Tudo
    </button>
  </div>

  <div class="toolbar-section rotation-section">
    <button (click)="rotateLeft()" title="Rotacionar -15°">⟲ -15°</button>
    <button (click)="rotateRight()" title="Rotacionar +15°">⟳ +15°</button>
  </div>

  <div class="toolbar-section layer-section">
    <button (click)="bringToFront()" title="Trazer para frente">⬆ Frente</button>
    <button (click)="sendToBack()" title="Enviar para trás">⬇ Trás</button>
  </div>

  <div class="toolbar-section group-section">
    <button (click)="group()" title="Agrupar selecionados">🧩 Agrupar</button>
    <button (click)="ungroup()" title="Desagrupar">🧩 Desagrupar</button>
  </div>

  <div class="toolbar-section align-section">
    <button (click)="align('left')" title="Alinhar à esquerda">⟸</button>
    <button (click)="align('center')" title="Centralizar horizontal">⇤⇥</button>
    <button (click)="align('right')" title="Alinhar à direita">⟹</button>
    <button (click)="align('top')" title="Alinhar ao topo">⇑</button>
    <button (click)="align('middle')" title="Centralizar vertical">⇞⇟</button>
    <button (click)="align('bottom')" title="Alinhar ao rodapé">⇓</button>
  </div>

  <div class="toolbar-section distribute-section">
    <button (click)="distribute('horizontal')" title="Distribuir horizontal">⟷</button>
    <button (click)="distribute('vertical')" title="Distribuir vertical">↕</button>
  </div>

  <div class="toolbar-section template-section">
    <button (click)="applyTemplate('sticky-notes')" title="Inserir notas adesivas">📝 Notas</button>
    <button (click)="applyTemplate('flow')" title="Inserir mini fluxograma">🧭 Fluxo</button>
  </div>

  <div class="toolbar-section grid-section">
    <label class="checkbox">
      <input type="checkbox" [checked]="snapToGrid" (change)="toggleSnapToGrid()" />
      Snap to grid
    </label>
    <div class="grid-size">
      <label>Tamanho da grade:</label>
      <input
        type="number"
        min="5"
        max="200"
        [value]="gridSize"
        (input)="onGridSizeChange(+$any($event.target).value)"
      />
      <span>{{ gridSize }}px</span>
    </div>
  </div>

  <div class="toolbar-section export-section">
    <button (click)="exportToPNG()" title="Exportar como PNG">
      💾 PNG
    </button>
    <button (click)="exportToJSON()" title="Exportar como JSON">
      💾 JSON
    </button>
    <button (click)="importFromJSON()" title="Importar JSON">
      📂 Importar
    </button>
  </div>

  <div class="toolbar-section theme-section">
    <button (click)="toggleTheme()" title="Alternar tema">
      {{ theme === 'light' ? '🌙 Dark' : '☀️ Light' }}
    </button>
  </div>
</div>
