  <div class="ai-chat__header">
    <span>ðŸ¤– Assistente IA (beta)</span>
    <button class="link" (click)="clearPrompt()" title="Limpar entrada">Limpar</button>
  </div>

  <textarea
    [(ngModel)]="prompt"
    placeholder="Descreva o fluxo: ex. InÃ­cio -> Autenticar -> DecisÃ£o -> Aprovar -> Fim"
    rows="4"
  ></textarea>

  <div class="ai-chat__actions">
    <button (click)="generate()" [disabled]="isBusy">{{ isBusy ? 'Gerando...' : 'Gerar diagrama' }}</button>
    <span class="ai-chat__message" *ngIf="message">{{ message }}</span>
  </div>
</div>
